function showOrdr(){

    var moneys = prompt("how many money do you have?");
    if (moneys > 10000) {
        car = '<img src = "data:image/jpeg;base64,"/>';
    } else if (moneys < 10000 && moneys > 5000) {

        car = '<img src = "data:image/jpeg;base64," width ="40%" height = "40%"/>';
    }
    else {
        car = '<img src = "https://upload.wikimedia.org/wikipedia/commons/thumb/2/25/Kia_Sephia_front_20081125.jpg/250px-Kia_Sephia_front_20081125.jpg" width ="40%" height = "40%"/>';
    }
    return car;
}
showOrdr();